validate.custom:
if(data.zip && data.zip != ''){
  if(data['zip'].length <= 12){
      valid = (data['zip'].match(/^[0-9\s-]*$/)) ?  true : 'Please enter a valid Zip Code for your region.';
  }else{
    valid = (data.zip.length > 12) ? 'Please enter a valid Zip Code for your region.' : true;
  }
}

if (data.dealershipPersonnel) {
  if (data.dealershipPersonnel.length == 1) {
    if (
      data.dealershipPersonnel[0].name !== "" ||
      data.dealershipPersonnel[0].ownershipPercentageNumber > 1
    ) {
      valid =
        data.dealershipPersonnel[0].ownershipPercentageNumber == 100
          ? true
          : "Ownership Percentage of the single member must be 100%";
    } else {
      valid = true;
    }
  } else if (data.dealershipPersonnel.length > 1) {
    valid =
      data.dealershipPersonnel
        .map((i) => i.ownershipPercentageNumber)
        .reduce((a, b) => a + b) == 100
        ? true
        : "Total Ownership Percentage of all the members cannot be less than or exceed 100%";
    if (valid == true) {
      valid =
        data.dealershipPersonnel.length ==
        new Set(data.dealershipPersonnel.map((i) => i.name)).size
          ? true
          : "You can only enter a name once.";
    }
  } else {
    valid = true;
  }
}

