    tempMonth = currentDate.month()
	 currentDate.month(5)
	}
    if((monthDifference - i) > 12 ){
        values[i] = {"label":currentDate.format("MMMM DD, YYYY") + ' - June 30, ' +(endYear - 1),"value": currentDate.format("YYYY-MM-DD")}
    }else{
        values[i] = {"label":currentDate.format("MMMM DD, YYYY") + ' - ' +endDate.format("MMMM DD, YYYY"),"value" : currentDate.format("YYYY-MM-DD")}
    }
    currentDate.add(1,'months')
}

